import React, { useState } from 'react';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';

const Cart = () => {
  const [modal, setModal] = useState(false);
  const toggle = () => setModal(!modal);
  const [cartCount, setCartCount] = useState(0);

  const addToCart = () => {
    setCartCount(cartCount + 1);
  }

  return (
    <div>
      <Button color="primary" onClick={toggle}>Cart <span class="badge badge-light">{cartCount}</span></Button>
      <Modal isOpen={modal} toggle={toggle}>
        <ModalHeader toggle={toggle}>Shopping Cart</ModalHeader>
        <ModalBody>
          {/* Add your cart items here */}
          Your cart is currently empty.
        </ModalBody>
        <ModalFooter>
          <Button color="secondary" onClick={toggle}>Continue Shopping</Button>
          <Button color="primary" onClick={toggle}>Checkout</Button>
        </ModalFooter>
      </Modal>
    </div>
  );
};

export default Cart;
